import java.math.BigInteger;
import java.lang.Math;

public class Main {

    public static void main(String[] args) {

        BigInteger base = new BigInteger("");

        BigInteger exp = new BigInteger("");

        BigInteger mod = new BigInteger("");


//        BigInteger base = new BigInteger("2");
//
//        BigInteger exp = new BigInteger("6");
//
//        BigInteger mod = new BigInteger("");

        int div = 1000000;
        long START, STOP, t1, t2, t3;

        FileWritter file = new FileWritter();

        SquareAndMultiply SaM = new SquareAndMultiply();
        MontgomeryLadder ML = new MontgomeryLadder();
        SlidingWindowMethod SWM = new SlidingWindowMethod();


        START = System.nanoTime();
        file.write(SaM.Exponentiation(base,exp,mod).toString(),"SaM");
        STOP = System.nanoTime();

        t1 = (STOP - START) / div;


        System.out.println("CZAS ALGORYTMU SQUARE & MULTIPLY: " + t1 + " milisekund\n");


        START = System.nanoTime();
        file.write(ML.Exponentiation(base,exp,mod).toString(), "ML");
        STOP = System.nanoTime();

        t2 = (STOP - START) / div;


        System.out.println("CZAS ALGORYTMU MONTGOMERY LADDER: " + t2 + " milisekund\n");

        START = System.nanoTime();
        file.write(SWM.Exponentiation(base,exp,mod).toString(), "ML");
        STOP = System.nanoTime();

        t3 = (STOP - START) / div;


        System.out.println("CZAS ALGORYTMU SLIDING WINDOW METHOD: " + t3 + " milisekund\n");


    }
}
